"use strict";
$(document).ready(function () {
    var e = .01 * window.innerHeight;
    document.documentElement.style.setProperty("--vh", "".concat(e, "px")), window.addEventListener("resize", function () {
        var e = .01 * window.innerHeight;
        document.documentElement.style.setProperty("--vh", "".concat(e, "px"))
    }), $(".header__open-btn").on("click", function () {
        $(".menu").hasClass("menu--close") ? $(".menu").removeClass("menu--close") : $(".menu").addClass("menu--close")
    }), $(".phone_mask").mask("+7(999)999-99-99"), $(".products__list--scroll").slick({
        dots: !0,
        dotsClass: "my-dots",
        arrows: !1,
        slidesToShow: 4,
        slidesToScroll: 1,
        infinite: !1,
        responsive: [{
            breakpoint: 769,
            settings: {
                slidesToShow: 2,
                slidesToScroll: 2
            }
        }]
    })
});